import React from 'react';
import Select from 'react-select';
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import dog_kennels from "images/dog_kennels.png"

const db_ids= [
  {label: 'dog_kennels', value: 'dog_kennels'},
  {label: 'flight_2', value: 'flight_2'},
  {label: 'pets_1', value: 'pets_1'},
  {label: 'concert_singer', value: 'concert_singer'},
  {label: 'museum_visit', value: 'museum_visit'},
  {label: 'battle_death', value: 'battle_death'},
  {label: 'student_transcripts_tracking', value: 'student_transcripts_tracking'},
  {label: 'singer', value: 'singer'},
  {label: 'cre_Doc_Template_Mgt', value: 'cre_Doc_Template_Mgt'},
  {label: 'world_1', value: 'world_1'},
  {label: 'employee_hire_evaluation', value: 'employee_hire_evaluation'},
  {label: 'network_1', value: 'network_1'},
  {label: 'poker_player', value: 'poker_player'},
  {label: 'real_estate_properties', value: 'real_estate_properties'},
  {label: 'course_teach', value: 'course_teach'},
  {label: 'voter_1', value: 'voter_1'},
  {label: 'wta_1', value: 'wta_1'},
  {label: 'orchestra', value: 'orchestra'},
  {label: 'car_1', value: 'car_1'},
  {label: 'tvshow', value: 'tvshow'}
]

class App extends React.Component {
  state = {
    db_id: '',
    nlq: '',
    result: '',
    schema_img: ''
  }

  handleDBChange = (e) => {
    this.setState({
      db_id: e.value,
      schema_img: dog_kennels
    })
  }

  handleNLQChange = (e) => {
    this.setState({
      nlq: e.target.value
    })
  }

  clicked = (e) => {
    this.setState({
      result: "  processing..."
    })
    if (this.state.db_id === '') {
      this.setState({
        result: "  plase specify db id"
      })
      return
    }

    axios.get('http://141.223.199.148:5000/service', {
      headers: { 
        'Access-Control-Allow-Origin': '*',
      },
      crossdomain: true,
      params: {db_id: this.state.db_id, question: this.state.nlq}
    }).then( response => {
      this.setState({
        result: response.data.result
      })
    })
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
            IRNet service
        </header>
        <Select options = {db_ids} 
          placeholder='Select DB ID'
          onChange={this.handleDBChange}
        />
        <img src={this.state.schema_img} alt="schema_img" />
        <div className='nlq'>
          <b> NLQ: </b>
          <input className='nlq_textbox'
            onChange={this.handleNLQChange}/>
          <button onClick={this.clicked}> RUN </button>
        </div>
        <div className='result'>
          <b> Result: </b> {this.state.result}
        </div>
      </div>
    );
  }
}

export default App;
